import React from 'react';
import { Link } from 'react-router-dom';
import './BlogList.css'; // Import your custom CSS for styling

const BlogList = ({ blogs, category }) => {
  if (blogs.length === 0) {
    return <p className="blog-list">No {category === 'blogs' ? '' : category} blogs found.</p>;
  }

  return (
    <div className="blog-list-container">
      <div className="blog-list">
        {blogs.map(blog => (
          <Link to={`/${category}/${blog._id}`} className="blog-link" key={blog._id}>
            <div className="blog-preview">
              <h2 className="blog-title">{blog.title}</h2>
              <p className="blog-author">Written by {blog.author}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default BlogList;
